function generateInputsFromJson($lang) {
    $jsonFile = WP_PLUGIN_DIR . '/Shahkar/modules/Lang/' . $lang . '.json';

    if (!file_exists($jsonFile)) {
        echo "File not found: " . htmlspecialchars($jsonFile);
        return;
    }

    $data = json_decode(file_get_contents($jsonFile), true);
    if ($data === null) {
        echo "Invalid JSON in file: " . htmlspecialchars($jsonFile);
        return;
    }

    foreach ($data as $key => $value) {
        $decodedValue = json_decode('"' . $value . '"');

        echo '<div class="half-input">';
        echo '<label><span>' . htmlspecialchars($lang) . ' ' . htmlspecialchars($key) . '</span>';
        echo '<input type="text" name="' . htmlspecialchars($lang) . '-' . htmlspecialchars($key) . '" value="' . htmlspecialchars($decodedValue) . '"></label>';
        echo '</div>';
    }
}

function saveLangData($lang, $data) {
    $jsonFile = WP_PLUGIN_DIR . '/Shahkar/modules/Lang/' . $lang . '.json';

    if (!file_exists($jsonFile)) {
        echo "File not found: " . htmlspecialchars($jsonFile);
        return;
    }

    file_put_contents($jsonFile, json_encode($data, JSON_PRETTY_PRINT | JSON_UNESCAPED_UNICODE));
}

if ($_SERVER["REQUEST_METHOD"] == "POST") {
    echo '<div class="updated"><p> با موفقیت ذخیره شد ! </p></div>';

    function removeLangPrefix($key) {
        return preg_replace('/^(fa|en|ar)-/', '', $key);
    }

    $englishData = [];
    $farsiData = [];
    $arabicData = [];

    foreach ($_POST as $key => $value) {
        if (strpos($key, 'fa-') === 0) {
            $farsiData[removeLangPrefix($key)] = $value;
        } elseif (strpos($key, 'en-') === 0) {
            $englishData[removeLangPrefix($key)] = $value;
        } elseif (strpos($key, 'ar-') === 0) {
            $arabicData[removeLangPrefix($key)] = $value;
        }
    }

    saveLangData('en', $englishData);
    saveLangData('fa', $farsiData);
    saveLangData('ar', $arabicData);
}
