<script>
$('.shahkar-lang-box').on('click', function() {
        var targetLang = $(this).data('target-lang');

        $('.shahkar-change-lang-box').removeClass('shahkar-active-lang-box');

        $('.shahkar-change-lang-box[data-shahkar-lang="' + targetLang + '"]').addClass('shahkar-active-lang-box');

        $('.shahkar-lang-box').removeClass('shahkar-active-lang');

        $(this).addClass('shahkar-active-lang');
    });
</script>